import React, { useCallback, useEffect, useState } from 'react'
import { Form, Button } from 'react-bootstrap';
import axios from 'axios'
import { AgGridReact } from 'ag-grid-react'
import 'ag-grid-community/styles/ag-grid.css'
import 'ag-grid-community/styles/ag-theme-quartz.css'
import '../AppGrid.css';
import WarehouseConfigForm from './WarehouseConfigForm'
import { useSkuForm } from './SkuForm';
import { SearchBar } from './SearchBar';

export const GridWarehouseConfig = () => {
    const { getData } = useSkuForm();

    const [gridApi, setGridApi] = useState(null);
    const [selectedRows, setSelectedRows] = useState([]);
    const [search, setSearch] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [rowData, setRowData] = useState([])
    const [colData, setColData] = useState(
        [
            // Update these column definitions to match your warehouse configuration data
            { headerName: 'Warehouse ID', field: 'warehouse_id', checkboxSelection: true, headerCheckboxSelection: true, headerCheckboxSelectionFilteredOnly: true },
            { headerName: 'Location', field: 'location', editable: true },
            // Add more columns as needed
        ]
    )

    window.row = rowData

    useEffect(() => {
        getData('warehouse-config').then(data => {
            setRowData(data);
        });
    }, []);

    const updateWarehouseConfigData = useCallback((param) => {
        setRowData([param, ...rowData])
    })

    const handleCellValueChanged = async (event) => {
        try {
            for (let key in event.data) {
                if (event.data[key] === undefined || event.data[key] === null) {
                    console.error(`Error: ${key} is empty`);
                    return;
                }
            }
            const response = await axios.patch(`http://localhost:8000/warehouse-config/${event.data.warehouse_id}`, event.data);
        } catch (error) {
            console.error('Error updating Warehouse Config data:', error);
        }
    }

    const getrowId = useCallback(params => {
        return params.data.warehouse_id
    })

    const onGridReady = params => {
        setGridApi(params.api);
    };

    const onSelectionChanged = (param) => {
        setSelectedRows(gridApi.getSelectedRows());
    };

    const onRowClicked = (event) => {
        event.node.setSelected(!event.node.isSelected());
    };

    window.trow = selectedRows

}
