import React, { useCallback, useEffect, useState } from 'react'
import { Form, Button } from 'react-bootstrap';
import axios from 'axios'
import 'ag-grid-community/styles/ag-grid.css'
import 'ag-grid-community/styles/ag-theme-quartz.css'
import '../AppGrid.css';
import { useSkuForm } from './CskuForm';
import { SearchBar } from './SearchBar';

const CustomSearch = ({ title }) => {

    const handleExport = () => {
        const title_name = title + '.xlsx';

        const transformedData = data.map((row, index) => {
            let transformedRow = {};
            for (let key in row) {
                let value = row[key];
                if (key == 'skus') {
                    console.log('value before join:', value);
                    console.log('value after join:', value.join(' '));
                    transformedRow[transformBack(key)] = value.join(' ');
                }
                else if (typeof value === 'object' || value === '' || value === null) {
                    continue;
                }
                else {
                    transformedRow[transformBack(key)] = value;
                }
            }
            return transformedRow;
        });

        const ws = XLSX.utils.json_to_sheet(transformedData);
        const wb = XLSX.utils.book_new();
        XLSX.utils.book_append_sheet(wb, ws, 'Sheet1');
        XLSX.writeFile(wb, title_name);

        console.log('exported data complete:')
    }

    const handleImport = async () => {
        try {
            const fileInput = document.createElement('input');
            fileInput.type = 'file';
            fileInput.accept = '.xlsx';
            fileInput.click();

            const filePromise = new Promise((resolve) => {
                fileInput.addEventListener('change', () => {
                    resolve(fileInput.files[0]);
                });
            });
            const file = await filePromise;
            if (file) {
                console.log('file:', file);
                const formData = new FormData();
                formData.append('file', file);
                formData.append('filename', title + '.xlsx');

                const response = await fetch('http://localhost:8000/upload', {
                    method: 'POST',
                    body: formData,
                });
                console.log('response: ', response);
                setRerender((prev) => !prev);
            }
        } catch (error) {
            console.error('Error uploading file:', error);
        }

    };


    return (
        <div className='d-flex justify-content-between'>
            <h1 style={{ fontFamily: 'Roboto', paddingTop: 16, textAlign: 'left' }} >
                {title}
            </h1>
            <div style={{ paddingTop: 16 }}>
                <button className="btn btn-dark"
                    style={{ marginLeft: '10px', fontSize: '20px', textAlign: 'center' }}
                    title={`Upload: '${title}'`}
                    onClick={handleImport}
                >
                    <i className="bi bi-upload"></i>
                </button>
                <button className="btn btn-dark"
                    style={{ marginLeft: '10px', fontSize: '20px', paddingTop: 6 }}
                    title={`Download:'${title}'`}
                    onClick={handleExport}
                >
                    <i className="bi bi-cloud-download"></i>
                </button>
            </div>
        </div>
    )
}

export const ConfigTable = () => {

    return (
        <div>
            <div className='boxing-day'>
                <CustomSearch title='Warehouse Main' />
                <div className='boxing-tide'>
                    <div>
                        <div className='boxing-title'>Origin</div>
                        <div className='boxing-item'>one</div>
                    </div>
                    <div>
                        <div className='boxing-title'>Product Tags</div>
                        <div className='boxing-item'>one two three four five six</div>
                    </div>
                    <div>
                        <div className='boxing-title'>Shipping Countries</div>
                        <div className='boxing-item'>one two three</div>
                    </div>
                </div>
            </div>
        </div>
    )
}
