function getDataByTag(warehouse, tags) {
  const resultArray = [];
  for (const key in warehouse) {
    if (tags.includes(key)) {
      if (!resultArray.includes(warehouse[key])) {
        resultArray.push({
          ...warehouse[key],
          product_tag: key
        });
      }
    }
  }
  return resultArray;
}

